import { Router } from 'express';
import * as faqController from '../controllers/faqController.js';
import { authMiddleware } from '../middlewares/authMiddleware.js';
const router = Router();
router.use(authMiddleware.authenticate);
router.post('/', authMiddleware.authorize('manage', 'Faqs'), faqController.createFaq);
router.get('/', authMiddleware.authorize('view', 'Faqs'), faqController.getFaqs);
router.put('/:id', authMiddleware.authorize('manage', 'Faqs'), faqController.updateFaq);
router.delete('/:id', authMiddleware.authorize('manage', 'Faqs'), faqController.deleteFaq);
router.get('/search', authMiddleware.authorize('view', 'Faqs'), faqController.searchFaqs);
export default router;
